# Función para extraer parte corta del diagnóstico
def extract_short(text):
    if pd.isna(text):
        return ""
    parts = re.split(r"\balso known as\b", text, flags=re.IGNORECASE)
    return parts[1].strip().rstrip(".") if len(parts) > 1 else ""

# Cargar CSV
df = pd.read_csv("data29/test_all.csv")

# Crear columna corta primero
df["golden-diagnosis"] = df["golden_diagnosis"].apply(extract_short)

# Renombrar la original como long
df = df.rename(columns={"golden_diagnosis": "golden-diagnosis-long"})

# Reordenar columnas para poner golden-diagnosis antes de golden-diagnosis-long
cols = list(df.columns)
golden_diag_idx = cols.index("golden-diagnosis")
golden_long_idx = cols.index("golden-diagnosis-long")

# Mover golden-diagnosis antes de golden-diagnosis-long
cols.insert(golden_long_idx, cols.pop(golden_diag_idx))
df = df[cols]

# Insertar columnas binarias después de 'diagnostic_code/s'
insert_at = df.columns.get_loc("diagnostic_code/s") + 1
for label, ids in FLAGS.items():
    df.insert(insert_at, label, df["id"].isin(ids).astype(int))
    insert_at += 1

# Guardar el archivo modificado
df.to_csv("data29/test_all_with_flags.csv", index=False)
print("✅ Archivo guardado como 'test_all_with_flags.csv'")
